import React, { useState, useEffect } from "react";
import axios from "axios";

function Header() {
  const [factVisible, setFactVisible] = useState(false);
  const [showFacts, setShowFacts] = useState(false);

  const toggleFacts = () => {
    setShowFacts(!showFacts);
  };

  useEffect(() => {
    const fetchData = async () => {
      try {
        const { data } = await axios.get("https://uselessfacts.jsph.pl/random.json?language=en");
        setFactVisible(data.show);
      } catch (error) {
        console.log("Failed to fetch facts visibility:", error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    if (factVisible !== null) {
      const updateFactsVisibility = async () => {
        try {
          await axios.put("https://uselessfacts.jsph.pl/random.json?language=en", { show: showFacts });
        } catch (error) {
          console.log("Failed to update facts visibility:", error);
        }
      };

      updateFactsVisibility();
    }
  }, [showFacts, factVisible]);

  return (
    <header>
      <h1>My App</h1>
      <button onClick={toggleFacts}>
        {showFacts ? "Hide Facts" : "Show Facts"}
      </button>
    </header>
  );
}

export default Header;
