import React, { useState } from "react";

function Navbar() {
  const [showLinks, setShowLinks] = useState(false);

  const toggleLinks = () => {
    setShowLinks(!showLinks);
  };

  const styles = {
    nav: {
      position: "fixed",
      top: 0,
      left: 0,
      width: "100%",
      backgroundColor: "#222",
      color: "#fff",
      padding: "10px",
      display: "flex",
      justifyContent: "space-between",
      alignItems: "center",
    },
    toggleBtn: {
      backgroundColor: "transparent",
      border: "none",
      color: "#fff",
      fontSize: "1.5rem",
      cursor: "pointer",
    },
    navLinks: {
      display: "flex",
      justifyContent: "center",
      alignItems: "center",
    },
    link: {
      color: "#fff",
      margin: "0 10px",
      textDecoration: "none",
      fontSize: "1.2rem",
      fontWeight: "bold",
      textTransform: "uppercase",
      left: 0,

    },
    show: {
      display: "block",
    },
    hide: {
      display: "none",
    },
  };

  return (
    <nav style={styles.nav}>
      <div className="nav-header">
        <button
          className="toggle-btn"
          style={styles.toggleBtn}
          onClick={toggleLinks}
        >
          <i className={showLinks ? "fas fa-times" : "fas fa-bars"}></i>
        </button>
      </div>
      <div
        className="nav-links"
        style={showLinks ? styles.show : styles.hide}
      >
        <ul style={styles.navLinks}>
          <li>
            <a href="/" style={styles.link}>
              Weather
            </a>
          </li>
          <li>
            <a href="/about" style={styles.link}>
              Chat
            </a>
          </li>
          <li>
            <a href="/contact" style={styles.link}>
              Random Fact
            </a>
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
