import React, { useEffect } from "react";
import { ChatBot, ThemeProvider } from "react-simple-chatbot";
import { ChatbotSteps } from "./ChatbotSteps";

const theme = {
  background: "#f5f8fb",
  headerBgColor: "#0084ff",
  headerFontColor: "#fff",
  headerFontSize: "15px",
  botBubbleColor: "#0084ff",
  botFontColor: "#fff",
  userBubbleColor: "#fff",
  userFontColor: "#4a4a4a",
};

const Chatbot = ({ setName }) => {
  useEffect(() => {
    localStorage.removeItem("city");
  }, []);

  return (
    <ThemeProvider theme={theme}>
      <ChatBot
        steps={ChatbotSteps({ setName })}
        hideHeader={true}
      />
    </ThemeProvider>
  );
};

export default Chatbot;
