import React, { useState, useEffect } from "react";
import { Box, Card, CardContent, makeStyles } from "@material-ui/core";
import logo from "../images/bg.jpg";
import Form from "./Form";
import ChatBot from "react-simple-chatbot";
import { ThemeProvider } from "styled-components";

const useStyles = makeStyles((theme) => ({
  component: {
    height: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    background: `linear-gradient(rgba(0,0,0,0.4), rgba(0,0,0,0.4)), url(${logo})`,
    backgroundSize: "cover",
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
    },
  },
  card: {
    display: "flex",
    borderRadius: "20px",
    width: "100%",
    height: "calc(100% - 40px)",
    [theme.breakpoints.up("md")]: {
      width: "80%",
    },
  },
  leftContainer: {
    width: "50%",
    background: "#0084ff",
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    justifyContent: "center",
    borderTopLeftRadius: "20px",
    borderBottomLeftRadius: "20px",
    color: "#fff",
    [theme.breakpoints.down("sm")]: {
      width: "100%",
      height: "30%",
      borderBottomLeftRadius: "0px",
      borderBottomRightRadius: "0px",
      borderTopRightRadius: "20px",
    },
  },
  rightContainer: {
    width: "50%",
    height: "100%",
    [theme.breakpoints.down("sm")]: {
      width: "100%",
      height: "70%",
    },
  },
  chatbot: {
    position: "absolute",
    bottom: "10px",
    left: "10px",
  },
}));


const theme = {
  background: "#f5f8fb",
  headerBgColor: "#0084ff",
  headerFontColor: "#fff",
  headerFontSize: "15px",
  botBubbleColor: "#0084ff",
  botFontColor: "#fff",
  userBubbleColor: "#fff",
  userFontColor: "#4a4a4a",
};

const ChatbotComponent = ({ onCitySubmit }) => {
  const [name, setName] = useState("");

  useEffect(() => {
    localStorage.removeItem("city");
  }, []);

  const steps = [    {      id: "1",      message: "Hi, what is your name?",      trigger: "name",    },    {      id: "name",      user: true,      trigger: "3",      validator: (value) => {        if (!value || value.trim() === "") {          return "Please enter your name.";        }        setName(value);        return true;      },    },    {      id: "3",      message: `Hi ${name}! Which city's weather would you like to see?`,
      trigger: "city",
    },
    {
      id: "city",
      user: true,
      trigger: "5",

      validator: (value) => {
        if (!value || value.trim() === "") {
          return "Please enter a city.";
        }
        localStorage.setItem("city", value); // set the city value in localStorage
        return true;
      },
    },
    {
      id: "5",
      message: `Fetching weather for ${localStorage.getItem("city")}...`,
      end: true, // end the chatbot dialog after this step
    },
  ];




  return (
    <ThemeProvider theme={theme}>
      <ChatBot steps={steps} hideHeader={true} />
    </ThemeProvider>
  );
};



const Weather = () => {
    const classes = useStyles();
    const city = localStorage.getItem("city") || "";

    return (

      <Box className={classes.component}>
        <Box className={classes.leftContainer}>
          <h1
            style={{
              color: "#fff",
              fontSize: "2.5rem",
              marginBottom: "1rem",
            }}
          >
            Weather App
          </h1>
          <p
            style={{
              color: "#fff",
              fontSize: "1.2rem",
              textAlign: "center",
              maxWidth: "80%",
            }}
          >
            Get real-time weather updates for any location
          </p>
        </Box>
        <Box className={classes.rightContainer}>
          <Form city={city} />
          <div className={classes.chatbot}>
            <ChatbotComponent />
            <Header />
            <Navbar />
          </div>
        </Box>
      </Box>
    );
  };




export default Weather
