import React, { useState, useEffect } from "react";
import axios from "axios";

function Header() {
  const [showFacts, setShowFacts] = useState(false);

  const toggleFacts = () => {
    setShowFacts(!showFacts);
  };

  useEffect(() => {
    const updateFactsVisibility = async () => {
      try {
        await axios.fetch("https://uselessfacts.jsph.pl/random.json?language=en", { show: showFacts });
      } catch (error) {
        console.log("Failed to update facts visibility:", error);
      }
    };

    updateFactsVisibility();
  }, [showFacts]);

  return (
    <header>
      <h1>My App</h1>
      <button onClick={toggleFacts}>
        {showFacts ? "Hide Facts" : "Show Facts"}
      </button>
    </header>
  );
}

export default Header;
