export const ChatbotSteps = ({ setName }) => [
    {
      id: "1",
      message: "Hi, what is your name?",
      trigger: "name",
    },
    {
      id: "name",
      user: true,
      trigger: "3",
      validator: (value) => {
        if (!value || value.trim() === "") {
          return "Please enter your name.";
        }
        setName(value);
        return true;
      },
    },
    {
      id: "3",
      message: `Hi {previousValue}! Which city's weather would you like to see?`,
      trigger: "city",
    },
    {
      id: "city",
      user: true,
      trigger: "5",
      validator: (value) => {
        if (!value || value.trim() === "") {
          return "Please enter a city.";
        }
        localStorage.setItem("city", value);
        return true;
      },
    },
    {
      id: "5",
      message: `Fetching weather for ${localStorage.getItem("city")}...`,
      end: true,
    },
  ];
