import React, { useState } from "react";
import { Box, makeStyles } from "@material-ui/core";
import logo from "../images/bg.jpg";
import Form from "./Form";
import ChatBot from "react-simple-chatbot";
import { ThemeProvider } from "styled-components";

const useStyles = makeStyles((theme) => ({
  component: {
    height: "100vh",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
    background: `linear-gradient(rgba(0,0,0,0.4), rgba(0,0,0,0.4)), url(${logo})`,
    backgroundSize: "cover",
    [theme.breakpoints.down("sm")]: {
      flexDirection: "column",
    },
  },
  leftContainer: {
    height: "100%",
    width: "50%",
    display: "flex",
    flexDirection: "column",
    justifyContent: "center",
    alignItems: "center",
    borderRadius: "20px 0 0 20px",
    [theme.breakpoints.down("sm")]: {
      width: "100%",
      height: "30%",
      borderRadius: "20px 20px 0 0",
    },
  },
  rightContainer: {
    background: "#534679",
    height: "60%",
    width: "50%",
    position: "relative",
    [theme.breakpoints.down("sm")]: {
      width: "100%",
      height: "70%",
    },
  },
  chatbot: {
    position: "absolute",
    bottom: "10px",
    left: "10px",
  },
}));

const theme = {
  background: "#f5f8fb",
  headerBgColor: "#0084ff",
  headerFontColor: "#fff",
  headerFontSize: "15px",
  botBubbleColor: "#0084ff",
  botFontColor: "#fff",
  userBubbleColor: "#fff",
  userFontColor: "#4a4a4a",
};

const ChatbotComponent = () => {
    const [name, setName] = useState('');
    const [city, setCity] = useState('');

    const steps = [
      {
        id: '1',
        message: 'Hi, what is your name?',
        trigger: 'name',
      },
      {
        id: 'name',
        user: true,
        trigger: '3',
        validator: (value) => {
          if (!value || value.trim() === '') {
            return 'Please enter your name.';
          }
          setName(value);
          return true;
        },
      },
      {
        id: '3',
        message: `Hi ${name}! Which city's weather would you like to see?`,
        trigger: 'city',
      },
      {
        id: 'city',
        user: true,
        trigger: '5',
        validator: (value) => {
          if (!value || value.trim() === '') {
            return 'Please enter a city.';
          }
          setCity(value);
          return true;
        },
      },
      {
        id: '5',
        message: `Fetching weather for ${city}...`,
        end: true,
      },
    ];

    return (
      <ThemeProvider theme={theme}>
        <ChatBot steps={steps} hideHeader={true} />
      </ThemeProvider>
    );
  };




  const Weather = () => {
    const classes = useStyles();

    return (
      <Box className={classes.component}>
        <Box className={classes.leftContainer}>
          <h1
            style={{
              color: '#fff',
              fontSize: '2.5rem',
              marginBottom: '1rem',
            }}
          >
            Weather App
          </h1>
          <p
            style={{
              color: '#fff',
              fontSize: '1.2rem',
              textAlign: 'center',
              maxWidth: '80%',
            }}
          >
            Get real-time weather updates for any location
          </p>
        </Box>
        <Box className={classes.rightContainer}>
          <Form />
          <div className={classes.chatbot}>
            <ChatbotComponent />
          </div>
        </Box>
      </Box>
    );
  };


export default Weather;
